mapper.select:
select
,
from discussion order by update_time desc limit #{pageSize} offset #{offset}

select

,

from discussion
where author_id = #{authorId}
order by update_time desc
limit #{pageSize} offset #{offset}

mapper.insert:
insert into discussion (id, author_id, title, 
  create_time, update_time, content
  )
values (#{id,jdbcType=BIGINT}, #{authorId,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update discussion
set author_id = #{authorId,jdbcType=VARCHAR},
  title = #{title,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=BIGINT},
  update_time = #{updateTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}
